import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import AdminDashboard from './dashboards/AdminDashboard';
import TeacherDashboard from './dashboards/TeacherDashboard';
import StudentDashboard from './dashboards/StudentDashboard';
import ParentDashboard from './dashboards/ParentDashboard';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
      } catch (e) {
        console.error("Error parsing stored user", e);
        localStorage.clear();
      }
    }
    setLoading(false);
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
    // Ensure token is saved if it exists in userData
    if (userData.token) {
      localStorage.setItem('token', userData.token);
    }
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.clear();
  };

  if (loading) return <div className="loading-screen">Loading Application...</div>;

  return (
    <Router>
      <Routes>
        {/* Login Route: Redirect to dashboard if already logged in */}
        <Route
          path="/login"
          element={!user ? <Login onLogin={handleLogin} /> : <Navigate to="/dashboard" replace />}
        />

        {/* Unified Dashboard Route */}
        <Route
          path="/dashboard"
          element={
            user ? (
              // Use lowercase check to be safe against backend string variations
              user.role?.toLowerCase() === 'admin' ? (
                <AdminDashboard user={user} onLogout={handleLogout} />
              ) : user.role?.toLowerCase() === 'teacher' ? (
                <TeacherDashboard user={user} onLogout={handleLogout} />
              ) : user.role?.toLowerCase() === 'student' ? (
                <StudentDashboard user={user} onLogout={handleLogout} />
              ) : user.role?.toLowerCase() === 'parent' ? (
                <ParentDashboard user={user} onLogout={handleLogout} />
              ) : (
                <div className="error-screen">
                  <h2>Access Denied</h2>
                  <p>Role "{user.role}" not recognized.</p>
                  <button onClick={handleLogout}>Back to Login</button>
                </div>
              )
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Root Redirect */}
        <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} replace />} />

        {/* 404 Catch-all */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
