import React, { useEffect, useState } from "react";
import { adminAPI } from "../api";
import "../App.css";

function AdminDashboard({ user, onLogout }) {
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalTeachers: 0,
    totalParents: 0,
    totalClasses: 0,
  });
  const [students, setStudents] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [parents, setParents] = useState([]);
  const [classes, setClasses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [tab, setTab] = useState("overview");
  const [error, setError] = useState("");

  // âœ… FIXED: loadData extracted for Refresh button
  const loadData = async () => {
    try {
      setLoading(true);
      setError("");

      const [studentsRes, teachersRes, parentsRes, classesRes] =
        await Promise.all([
          adminAPI.getStudents(),
          adminAPI.getTeachers(),
          adminAPI.getParents(),
          adminAPI.getClasses(),
        ]);

      const studentsData = studentsRes.data || [];
      const teachersData = teachersRes.data || [];
      const parentsData = parentsRes.data || [];
      const classesData = classesRes.data || [];

      setStudents(studentsData);
      setTeachers(teachersData);
      setParents(parentsData);
      setClasses(classesData);

      setStats({
        totalStudents: studentsData.length,
        totalTeachers: teachersData.length,
        totalParents: parentsData.length,
        totalClasses: classesData.length,
      });
    } catch (err) {
      console.error(err);
      setError("Failed to load admin data");
    } finally {
      setLoading(false);
    }
  };

  // âœ… Simplified useEffect
  useEffect(() => {
    loadData();
  }, []);

  const renderOverview = () => (
    <div className="admin-overview-grid">
      <div className="stat-card">
        <h3>Total Students</h3>
        <p>{stats.totalStudents}</p>
      </div>
      <div className="stat-card">
        <h3>Total Teachers</h3>
        <p>{stats.totalTeachers}</p>
      </div>
      <div className="stat-card">
        <h3>Total Parents</h3>
        <p>{stats.totalParents}</p>
      </div>
      <div className="stat-card">
        <h3>Total Classes</h3>
        <p>{stats.totalClasses}</p>
      </div>
      {/* âœ… REFRESH BUTTON - Works now! */}
      <div className="stat-card full-width">
        <button onClick={loadData} className="btn btn-primary">
          ðŸ”„ Refresh Data
        </button>
      </div>
    </div>
  );

  const renderStudents = () => (
    <div className="table-wrapper">
      <h3>Students</h3>
      <table className="data-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Class</th>
            <th>Roll</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {students.length === 0 && (
            <tr>
              <td colSpan={4}>No students found</td>
            </tr>
          )}
          {students.map((s) => (
            <tr key={s.id}>
              <td>{`${s.first_name || ""} ${s.last_name || ""}`.trim()}</td>
              <td>
                {s.class_name}
                {s.section ? ` - ${s.section}` : ""}
              </td>
              <td>{s.roll_number}</td>
              <td>{s.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderTeachers = () => (
    <div className="table-wrapper">
      <h3>Teachers</h3>
      <table className="data-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Department</th>
            <th>Phone</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {teachers.length === 0 && (
            <tr>
              <td colSpan={4}>No teachers found</td>
            </tr>
          )}
          {teachers.map((t) => (
            <tr key={t.id}>
              <td>{`${t.first_name || ""} ${t.last_name || ""}`.trim()}</td>
              <td>{t.department}</td>
              <td>{t.phone}</td>
              <td>{t.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderParents = () => (
    <div className="table-wrapper">
      <h3>Parents</h3>
      <table className="data-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Children</th>
            <th>Phone</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {parents.length === 0 && (
            <tr>
              <td colSpan={4}>No parents found</td>
            </tr>
          )}
          {parents.map((p) => (
            <tr key={p.id}>
              <td>{`${p.first_name || ""} ${p.last_name || ""}`.trim()}</td>
              <td>
                {Array.isArray(p.children) && p.children.length > 0
                  ? p.children.map((c) => c.name).join(", ")
                  : "â€”"}
              </td>
              <td>{p.phone}</td>
              <td>{p.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderClasses = () => (
    <div className="table-wrapper">
      <h3>Classes</h3>
      <table className="data-table">
        <thead>
          <tr>
            <th>Class</th>
            <th>Section</th>
            <th>Teacher</th>
            <th>Students</th>
          </tr>
        </thead>
        <tbody>
          {classes.length === 0 && (
            <tr>
              <td colSpan={4}>No classes found</td>
            </tr>
          )}
          {classes.map((c) => (
            <tr key={c.id}>
              <td>{c.class_name}</td>
              <td>{c.section}</td>
              <td>{c.teacher_name || "Unassigned"}</td>
              <td>{c.student_count}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  // if (loading) {
  //   return (
  //     <div className="dashboard-container">
  //       <div className="dashboard-header">
  //         <h2>Admin Dashboard</h2>
  //       </div>
  //       <p>Loading...</p>
  //     </div>
  //   );
  // }
  if (loading) {
  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
      </div>
      <div className="skeleton-loader">
        <div className="admin-overview-grid">
          <div className="skeleton-stat"></div>
          <div className="skeleton-stat"></div>
          <div className="skeleton-stat"></div>
          <div className="skeleton-stat"></div>
        </div>
        <div className="skeleton-table">
          <div className="skeleton-row"></div>
          <div className="skeleton-row"></div>
          <div className="skeleton-row"></div>
        </div>
      </div>
    </div>
  );
}


  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <div className="dashboard-user-info">
          <span>{user?.email}</span>
          <button onClick={onLogout} className="btn btn-secondary">
            Logout
          </button>
        </div>
      </div>

      {error && <div className="error-banner">{error}</div>}

      <div className="dashboard-tabs">
        <button
          className={tab === "overview" ? "tab active" : "tab"}
          onClick={() => setTab("overview")}
        >
          Overview
        </button>
        <button
          className={tab === "students" ? "tab active" : "tab"}
          onClick={() => setTab("students")}
        >
          Students
        </button>
        <button
          className={tab === "teachers" ? "tab active" : "tab"}
          onClick={() => setTab("teachers")}
        >
          Teachers
        </button>
        <button
          className={tab === "parents" ? "tab active" : "tab"}
          onClick={() => setTab("parents")}
        >
          Parents
        </button>
        <button
          className={tab === "classes" ? "tab active" : "tab"}
          onClick={() => setTab("classes")}
        >
          Classes
        </button>
      </div>

      <div className="dashboard-content">
        {tab === "overview" && renderOverview()}
        {tab === "students" && renderStudents()}
        {tab === "teachers" && renderTeachers()}
        {tab === "parents" && renderParents()}
        {tab === "classes" && renderClasses()}
      </div>
    </div>
  );
}

export default AdminDashboard;
