import React, { useState, useEffect, useMemo } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import AdminDashboard from './dashboards/AdminDashboard';
import TeacherDashboard from './dashboards/TeacherDashboard';
import StudentDashboard from './dashboards/StudentDashboard';
import ParentDashboard from './dashboards/ParentDashboard';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Load user from storage on initial boot
  useEffect(() => {
    const hydrateAuth = () => {
      try {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
          setUser(JSON.parse(storedUser));
        }
      } catch (error) {
        console.error("Failed to parse stored user:", error);
        localStorage.clear();
      } finally {
        setLoading(false);
      }
    };

    hydrateAuth();
  }, []);

  const handleLogin = (userData) => {
    // 1. Sync state
    setUser(userData);
    // 2. Persist to storage
    localStorage.setItem('user', JSON.stringify(userData));
    if (userData.token) {
      localStorage.setItem('token', userData.token);
    }
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.clear();
  };

  // Helper function to decide which dashboard to show
  const getDashboardByRole = (userRole) => {
    const role = userRole?.toLowerCase();
    switch (role) {
      case 'admin': return <AdminDashboard user={user} onLogout={handleLogout} />;
      case 'teacher': return <TeacherDashboard user={user} onLogout={handleLogout} />;
      case 'student': return <StudentDashboard user={user} onLogout={handleLogout} />;
      case 'parent': return <ParentDashboard user={user} onLogout={handleLogout} />;
      default: return (
        <div className="error-container">
          <h2>Unauthorized Role</h2>
          <p>Your account is not assigned a valid role ({userRole}).</p>
          <button onClick={handleLogout}>Return to Login</button>
        </div>
      );
    }
  };

  if (loading) {
    return <div className="app-loader">Initializing System...</div>;
  }

  return (
    <Router>
      <Routes>
        {/* If logged in, don't show login page, go straight to dashboard */}
        <Route
          path="/login"
          element={!user ? <Login onLogin={handleLogin} /> : <Navigate to="/dashboard" replace />}
        />

        {/* Unified Dashboard Route with Guard */}
        <Route
          path="/dashboard/*"
          element={
            user ? (
              getDashboardByRole(user.role)
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />

        {/* Redirect root based on auth status */}
        <Route 
          path="/" 
          element={<Navigate to={user ? "/dashboard" : "/login"} replace />} 
        />

        {/* Catch-all: Redirect unknown routes to home */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
