};

// ML endpoints
export const mlAPI = {
  trainModel: () => api.post('/ml/train'),
  predictPerformance: studentId => api.get(`/ml/predict/${studentId}`),
  getWeakStudents: () => api.get('/ml/weak-students'),
  getClassPredictions: classId => api.get(`/ml/class-predictions/${classId}`)
};
export default api;

