import React, { useState, useEffect, useCallback } from 'react';
import { adminAPI, mlAPI } from '../services/api';
import '../dashboards/StudentDashboard.css';

function AdminDashboard({ user, onLogout }) {
  const [activeTab, setActiveTab] = useState('overview');
  const [students, setStudents] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [weakStudents, setWeakStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      // We use individual try/catch or check results to prevent one failed call from breaking everything
      const [studentsRes, teachersRes, classesRes, weakRes] = await Promise.all([
        adminAPI.getStudents().catch(e => ({ data: [] })),
        adminAPI.getTeachers().catch(e => ({ data: [] })),
        adminAPI.getClasses().catch(e => ({ data: [] })),
        mlAPI.getWeakStudents().catch(e => ({ data: [] })),
      ]);

      setStudents(Array.isArray(studentsRes.data) ? studentsRes.data : []);
      setTeachers(Array.isArray(teachersRes.data) ? teachersRes.data : []);
      setClasses(Array.isArray(classesRes.data) ? classesRes.data : []);
      setWeakStudents(Array.isArray(weakRes.data) ? weakRes.data : []);

      setError(null);
    } catch (err) {
      console.error('Final Catch:', err);
      setError("Critical error loading data.");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // If loading is true, show this. If this is missing, you get a blank screen.
  if (loading) return <div style={{padding: '50px', textAlign: 'center'}}>Loading Dashboard...</div>;

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>Admin Dashboard</h1>
          <div className="user-info">
            {/* SAFE ACCESS TO USER EMAIL */}
            <span>{user?.email || "Admin User"}</span>
            <button onClick={onLogout} className="logout-btn">Logout</button>
          </div>
        </div>
      </header>

      {error && <div className="error-message">{error}</div>}

      <div className="dashboard-tabs">
        <button className={activeTab === 'overview' ? 'active' : ''} onClick={() => setActiveTab('overview')}>Overview</button>
        <button className={activeTab === 'students' ? 'active' : ''} onClick={() => setActiveTab('students')}>Students</button>
      </div>

      <div className="dashboard-content">
        {activeTab === 'overview' && (
          <div className="stats-grid">
            <div className="stat-card">
              <h3>Total Students</h3>
              <div className="stat-value">{students?.length || 0}</div>
            </div>
            <div className="stat-card">
              <h3>At-Risk Students</h3>
              <div className="stat-value" style={{color: 'red'}}>{weakStudents?.length || 0}</div>
            </div>
          </div>
        )}

        {activeTab === 'students' && (
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                </tr>
              </thead>
              <tbody>
                {students.length > 0 ? students.map((s) => (
                  <tr key={s.id || s._id}>
                    <td>{s.first_name} {s.last_name}</td>
                    <td>{s.email}</td>
                  </tr>
                )) : <tr><td>No students found.</td></tr>}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
