// frontend/src/components/CGPACalculator.js - FIXED VERSION
import React, { useState, useEffect, useCallback } from "react";
import { cgpaAPI } from "../api";
import "./CGPACalculator.css";

function CGPACalculator({ isTeacher = false, className = "" }) {
  const [studentId, setStudentId] = useState("");
  const [semester, setSemester] = useState("2024-S1");
  const [currentSubjectCode, setCurrentSubjectCode] = useState(""); // üî• NEW STATE
  const [currentGrade, setCurrentGrade] = useState(""); // üî• NEW STATE
  const [marks, setMarks] = useState({});
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showConfirmModal, setShowConfirmModal] = useState(false); // üî• NEW
  const [confirmAction, setConfirmAction] = useState(null); // üî• NEW

  // GRADE DISPLAY
  const GRADE_DISPLAY = {
    'O': 'Outstanding (10)', 'A+': 'Excellent (9)', 'A': 'Very Good (8)',
    'B+': 'Good (7)', 'B': 'Average (6)', 'C': 'Pass (5)',
    'F': 'Fail (0)', 'SA': 'Shortage (0)', 'W': 'Withdrawal (0)'
  };

  // Load saved data
  useEffect(() => {
    const savedKey = `cgpa_${studentId}_${semester}`;
    const saved = localStorage.getItem(savedKey);
    if (saved) {
      const parsed = JSON.parse(saved);
      setResult(parsed);
      setMarks(parsed.marks || {});
    }
  }, [studentId, semester]);

  // üî• FIXED: Add Mark with State
  const addMark = useCallback(() => {
    if (!currentSubjectCode || !currentGrade) {
      alert("Please enter both subject code and grade!");
      return;
    }

    const subjectCode = currentSubjectCode.toUpperCase().trim();
    const grade = currentGrade.toUpperCase().trim();

    if (marks[subjectCode]) {
      alert(`Subject ${subjectCode} already exists!`);
      return;
    }

    setMarks(prev => ({
      ...prev,
      [subjectCode]: grade
    }));

    // Clear inputs
    setCurrentSubjectCode("");
    setCurrentGrade("");
  }, [currentSubjectCode, currentGrade, marks]);

  const removeMark = (subjectCode) => {
    setMarks(prev => {
      const newMarks = { ...prev };
      delete newMarks[subjectCode];
      return newMarks;
    });
  };

  const handleCalculate = async () => {
    if (Object.keys(marks).length === 0) {
      alert("Add at least one subject first! üìö");
      return;
    }

    if (!studentId) {
      alert("Enter Student ID! üÜî");
      return;
    }

    setLoading(true);
    try {
      const payload = {
        student_id: parseInt(studentId),
        marks: marks
      };

      console.log("üöÄ Sending to Flask:", payload); // DEBUG

      const response = await cgpaAPI.calculateStudentCGPA(payload);

      console.log("‚úÖ Flask Response:", response.data); // DEBUG

      if (response.data.error) {
        alert(`Error: ${response.data.error}`);
        return;
      }

      const fullResult = {
        ...response.data,
        marks,
        semester
      };

      setResult(fullResult);

      // Save locally
      const savedKey = `cgpa_${studentId}_${semester}`;
      localStorage.setItem(savedKey, JSON.stringify(fullResult));

      alert(`‚úÖ CGPA Calculated!\nCurrent GPA: ${fullResult.current_gpa}\nCGPA: ${fullResult.cgpa}`);

    } catch (error) {
      console.error("üî¥ CGPA Error:", error.response?.data || error.message);
      alert(`Failed to calculate CGPA:\n${error.response?.data?.error || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const clearAll = () => {
    if (confirm("Clear all marks and results?")) {
      setMarks({});
      setResult(null);
      setCurrentSubjectCode("");
      setCurrentGrade("");
      localStorage.removeItem(`cgpa_${studentId}_${semester}`);
    }
  };

  // üî• MARKS COUNT
  const marksCount = Object.keys(marks).length;

  return (
    <div className={`cgpa-calculator ${className}`}>
      <div className="cgpa-header">
        <h2>üßÆ CGPA Calculator</h2>
        <div className="input-row">
          <input
            type="number"
            placeholder="üÜî Student ID (e.g., 12345)"
            value={studentId}
            onChange={(e) => setStudentId(e.target.value)}
            className="student-id-input"
          />
          <select 
            value={semester} 
            onChange={(e) => setSemester(e.target.value)}
            className="semester-select"
          >
            <option value="2024-S1">2024 Semester 1</option>
            <option value="2024-S2">2024 Semester 2</option>
            <option value="2025-S1">2025 Semester 1</option>
          </select>
        </div>
        <div className="marks-counter">
          üìö {marksCount} subjects added
        </div>
      </div>

      {/* üî• FIXED MARKS INPUT - STATE DRIVEN */}
      <div className="marks-input-section">
        <h3>‚ûï Add Subject Mark</h3>
        <div className="input-pair">
          <input
            type="text"
            placeholder="Subject Code (e.g., HS3152, CS3351)"
            value={currentSubjectCode}
            onChange={(e) => setCurrentSubjectCode(e.target.value)}
            className="subject-input"
            onKeyDown={(e) => e.key === 'Enter' && document.querySelector('.grade-select')?.focus()}
          />
          <select 
            value={currentGrade}
            onChange={(e) => setCurrentGrade(e.target.value)}
            className="grade-select"
            onKeyDown={(e) => e.key === 'Enter' && addMark()}
          >
            <option value="">Select Grade</option>
            {Object.entries(GRADE_DISPLAY).map(([grade, display]) => (
              <option key={grade} value={grade}>{display}</option>
            ))}
          </select>
        </div>

        <div className="marks-actions">
          <button 
            onClick={addMark} 
            disabled={!currentSubjectCode || !currentGrade}
            className="add-btn"
          >
            ‚ûï Add Subject ({marksCount + 1})
          </button>
          <button onClick={handleCalculate} disabled={loading || marksCount === 0 || !studentId}>
            {loading ? "üîÑ Calculating..." : `üöÄ Calculate CGPA (${marksCount} subjects)`}
          </button>
          <button onClick={clearAll} className="clear-btn">
            üóëÔ∏è Clear All
          </button>
        </div>
      </div>

      {/* MARKS LIST */}
      {marksCount > 0 && (
        <div className="marks-list">
          <h3>üìã Current Marks ({marksCount} subjects)</h3>
          {Object.entries(marks).map(([code, grade]) => (
            <div key={code} className="mark-item">
              <span className="mark-code">{code}</span>
              <span className={`grade-badge ${grade.toLowerCase().replace('+', 'plus')}`}>
                {grade}
              </span>
              <button 
                onClick={() => removeMark(code)}
                className="remove-mark"
                title="Remove subject"
              >
                √ó
              </button>
            </div>
          ))}
        </div>
      )}

      {/* RESULTS */}
      {result && (
        <div className="results-card">
          <h3>üìä Results for {semester}</h3>
          <div className="key-results">
            <div className="result-item">
              <span>Current GPA (This Semester):</span>
              <strong>{result.current_gpa?.toFixed(2) || 'N/A'}</strong>
            </div>
            <div className="result-item">
              <span>Overall CGPA:</span>
              <strong>{result.cgpa?.toFixed(2) || 'N/A'}</strong>
            </div>
            <div className="result-item">
              <span>Semester Credits:</span>
              <strong>{result.semester_credits || 0}</strong>
            </div>
          </div>

          {result.updated_gpa_list && (
            <div className="gpa-history">
              <h4>üìà GPA History</h4>
              <div className="history-grid">
                {result.updated_gpa_list.map((gpa, idx) => (
                  <div key={idx} className="history-item">
                    <span>S{idx + 1}</span>
                    <span>{gpa.toFixed(2)}</span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}
    </div>

  );

}

export default CGPACalculator;
