import React, { useState } from 'react';
import { authAPI } from '../services/api';
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import './Login.css';

function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

// const handleSubmit = async (e) => {
//   e.preventDefault();
//   setLoading(true);
//   setError("");



//   try {
//     const res = await axios.post(
//       "http://localhost:5000/api/auth/login",
//       { email, password }
//     );

//     console.log("DATA:", res.data);

//     // ✅ If backend returns user + token
//     if (res.data.user && res.data.token) {
//       localStorage.setItem("user", JSON.stringify(res.data.user));
//       localStorage.setItem("token", res.data.token);
//     }

//     // ✅ If backend returns everything directly
//     else if (res.data.token) {
//       localStorage.setItem("user", JSON.stringify(res.data));
//       localStorage.setItem("token", res.data.token);
//     }

//     else {
//       alert("Login response invalid");
//       return;
//     }
//     console.log("FULL RESPONSE:", res);
//     console.log("DATA:", res.data);
//     console.log("Saved successfully!");
//     navigate("/dashboard");   // better than window.location

//   } catch (error) {
//     console.error("LOGIN ERROR:", error.response?.data || error.message);
//     setError("Login failed");
//   } finally {
//     setLoading(false);
//   }
// };


  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('error');
    setLoading(true);

    try {
      const response = await authAPI.login(email, password);
      onLogin({...response.data.user,token: response.data.token});

      // navigate('/dashboard/admin');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <h1>Student Evaluation System</h1>
          <p>Sign in to continue</p>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Signing in...' : 'Sign In'}
          </button>
        </form>

        <div className="login-footer">
          <p>Default credentials:</p>
          <p>Admin: admin@school.com / admin123</p>
        </div>
      </div>
    </div>
  );
}

export default Login;
