import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { authAPI } from '../services/api';
import './Login.css';

function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const res = await authAPI.login(email, password);

      // Extract data safely
      const { user, token } = res.data;
      if (!res.data.token) {
        setError("Invalid login response from server");
        return;
      }

      // If backend sends { user, token } OR sends full user with token
      const userData = res.data.user || res.data;

      // Save to localStorage
      // localStorage.setItem("user", JSON.stringify(userData));
      localStorage.setItem("token", res.data.token);

      // Update React app state
      onLogin({user: res.data.user,token: res.data.token});
      const targetPath = user.role ? `/dashboard/${user.role}` : "/dashboard";
      navigate(targetPath);

      // Redirect to dashboard

    } catch (err) {
      console.error("LOGIN ERROR:", err.response?.data || err.message);
      setError(err.response?.data?.error || "Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };
  //     if (!token) {
  //       throw new Error("Invalid login response from server.");
  //     }

  //     // 1. Persist to LocalStorage for session recovery on refresh
  //     localStorage.setItem("token", token);
  //     localStorage.setItem("user", JSON.stringify(user));

  //     // 2. Update the Global Auth State (Crucial for Protected Routes)
  //     if (onLogin) {
  //       onLogin({ ...user, token });
  //     }

  //     // 3. Navigate based on role (optional) or to a general dashboard
  //     // If your evaluation system has different views:
  //     const targetPath = user.role ? `/dashboard/${user.role}` : "/dashboard";
  //     navigate(targetPath);

  //   } catch (err) {
  //     console.error("LOGIN ERROR:", err);
  //     setError(err.response?.data?.message || "Login failed. Check your credentials.");
  //   } finally {
  //     setLoading(false);
  //   }
  // };

  return (
    <div className="login-container">
      <div className="login-box">
        <header className="login-header">
          <h1>Student Evaluation System</h1>
          <p>Sign in to continue</p>
        </header>

        {error && <div className="error-message" role="alert">{error}</div>}

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              autoComplete="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="admin@school.com"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              autoComplete="current-password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="••••••••"
            />
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Authenticating...' : 'Sign In'}
          </button>
        </form>

        <footer className="login-footer">
          <p><strong>Default:</strong> admin@school.com / admin123</p>
        </footer>
      </div>
    </div>
  );
}

export default Login;


