// Teacher Dashboard
import React, { useState, useEffect } from 'react';
import { teacherAPI, adminAPI } from '../../services/api';

export function TeacherDashboard({ user, onLogout }) {
  const [activeTab, setActiveTab] = useState('overview');
  const [classes, setClasses] = useState([]);
  // const [selectedClass, setSelectedClass] = useState(null);
  const [performance, setPerformance] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const classesRes = await adminAPI.getClasses();
      setClasses(classesRes.data);
      if (classesRes.data.length > 0) {
        // setSelectedClass(classesRes.data[0].id);
        const perfRes = await teacherAPI.getClassPerformance(classesRes.data[0].id);
        setPerformance(perfRes.data);
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>Teacher Dashboard</h1>
          <div className="user-info">
            <span>{user.email}</span>
            <button onClick={onLogout} className="logout-btn">Logout</button>
          </div>
        </div>
      </header>

      <div className="dashboard-tabs">
        <button className={`tab ${activeTab === 'overview' ? 'active' : ''}`} onClick={() => setActiveTab('overview')}>Overview</button>
        <button className={`tab ${activeTab === 'marks' ? 'active' : ''}`} onClick={() => setActiveTab('marks')}>Marks Entry</button>
        <button className={`tab ${activeTab === 'attendance' ? 'active' : ''}`} onClick={() => setActiveTab('attendance')}>Attendance</button>
      </div>

      <div className="dashboard-content">
        <div className="stats-grid">
          <div className="stat-card">
            <h3>Total Classes</h3>
            <div className="stat-value">{classes.length}</div>
          </div>
          <div className="stat-card">
            <h3>Total Students</h3>
            <div className="stat-value">{performance.length}</div>
          </div>
        </div>

        {activeTab === 'overview' && (
          <div className="marks-section">
            <h2>Class Performance</h2>
            <div className="table-container">
              <table className="data-table">
                <thead>
                  <tr>
                    <th>Roll No</th>
                    <th>Student Name</th>
                    <th>Average %</th>
                    <th>Subjects</th>
                    <th>Total Exams</th>
                    <th>Attendance</th>
                  </tr>
                </thead>
                <tbody>
                  {performance.map((student) => (
                    <tr key={student.id}>
                      <td>{student.roll_number}</td>
                      <td>{student.student_name}</td>
                      <td>
                        <span className={`percentage ${student.average_percentage >= 50 ? 'pass' : 'fail'}`}>
                          {student.average_percentage?.toFixed(1) || 0}%
                        </span>
                      </td>
                      <td>{student.subjects_taken || 0}</td>
                      <td>{student.total_exams || 0}</td>
                      <td>{student.present_days || 0}/{student.total_days || 0}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// Parent Dashboard
export function ParentDashboard({ user, onLogout }) {
  const [children, setChildren] = useState([]);
  // const [selectedChild, setSelectedChild] = useState(null);
  const [marks, setMarks] = useState([]);
  const [attendance, setAttendance] = useState({ records: [], summary: {} });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const { parentAPI } = await import('../../services/api');
      const childrenRes = await parentAPI.getMyChildren();
      setChildren(childrenRes.data);

      if (childrenRes.data.length > 0) {
        const childId = childrenRes.data[0].id;
        // setSelectedChild(childId);
        const [marksRes, attendanceRes] = await Promise.all([
          parentAPI.getChildMarks(childId),
          parentAPI.getChildAttendance(childId),
        ]);
        setMarks(marksRes.data);
        setAttendance(attendanceRes.data);
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>Parent Dashboard</h1>
          <div className="user-info">
            <span>{user.email}</span>
            <button onClick={onLogout} className="logout-btn">Logout</button>
          </div>
        </div>
      </header>

      <div className="dashboard-content">
        <div className="stats-grid">
          <div className="stat-card">
            <h3>My Children</h3>
            <div className="stat-value">{children.length}</div>
          </div>
          <div className="stat-card">
            <h3>Attendance</h3>
            <div className="stat-value">{attendance.summary?.attendance_percentage || 0}%</div>
          </div>
          <div className="stat-card">
            <h3>Total Marks Entries</h3>
            <div className="stat-value">{marks.length}</div>
          </div>
        </div>

        <div className="marks-section">
          <h2>Children</h2>
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Class</th>
                  <th>Relationship</th>
                </tr>
              </thead>
              <tbody>
                {children.map((child) => (
                  <tr key={child.id}>
                    <td>{child.first_name} {child.last_name}</td>
                    <td>{child.class_name} {child.section}</td>
                    <td>{child.relationship}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        <div className="marks-section">
          <h2>Recent Marks</h2>
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Subject</th>
                  <th>Exam Type</th>
                  <th>Marks</th>
                  <th>Percentage</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {marks.slice(0, 10).map((mark) => (
                  <tr key={mark.id}>
                    <td>{mark.subject_name}</td>
                    <td>{mark.exam_type}</td>
                    <td>{mark.marks_obtained} / {mark.max_marks}</td>
                    <td>
                      <span className={`percentage ${mark.percentage >= 50 ? 'pass' : 'fail'}`}>
                        {mark.percentage?.toFixed(1)}%
                      </span>
                    </td>
                    <td>{new Date(mark.exam_date).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TeacherDashboard;
