import React, { useState, useEffect } from 'react';
import { adminAPI, mlAPI } from '../services/api';
import '../dashboards/StudentDashboard.css';

function AdminDashboard({ user, onLogout }) {
  const [activeTab, setActiveTab] = useState('overview');
  const [students, setStudents] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [weakStudents, setWeakStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);

      const [studentsRes, teachersRes, classesRes, weakRes] = await Promise.all([
        adminAPI.getStudents(),
        adminAPI.getTeachers(),
        adminAPI.getClasses(),
        mlAPI.getWeakStudents(),
      ]);

      setStudents(studentsRes.data || []);
      setTeachers(teachersRes.data || []);
      setClasses(classesRes.data || []);
      setWeakStudents(weakRes.data || []);
    } catch (err) {
      console.error('Error fetching admin data:', err);
      setError('Failed to load dashboard data.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="loading" style={{ height: "100vh" }}>
        Loading Admin Dashboard...
      </div>
    );
  }

  if (error) {
    return (
      <div className="loading">
        {error}
        <br />
        <button onClick={fetchData}>Retry</button>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>Admin Dashboard</h1>
          <div className="user-info">
            <span>{user?.email}</span>
            <button onClick={fetchData} className="logout-btn">Refresh</button>
            <button onClick={onLogout} className="logout-btn">Logout</button>
          </div>
        </div>
      </header>

      <div className="dashboard-tabs">
        {['overview', 'students', 'teachers', 'classes'].map(tab => (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      <div className="dashboard-content">

        {/* OVERVIEW */}
        {activeTab === 'overview' && (
          <div className="overview-section">

            <div className="stats-grid">
              <div className="stat-card"><h3>Total Students</h3><div className="stat-value">{students.length}</div></div>
              <div className="stat-card"><h3>Total Teachers</h3><div className="stat-value">{teachers.length}</div></div>
              <div className="stat-card"><h3>Total Classes</h3><div className="stat-value">{classes.length}</div></div>
              <div className="stat-card"><h3>At-Risk Students</h3><div className="stat-value">{weakStudents.length}</div></div>
            </div>

            <div className="chart-container">
              <h3>Students Needing Attention (ML Predictions)</h3>
              <div className="table-container">
                <table className="data-table">
                  <thead>
                    <tr>
                      <th>Student</th>
                      <th>Average</th>
                      <th>Risk</th>
                      <th>Attendance</th>
                    </tr>
                  </thead>
                  <tbody>
                    {weakStudents.map(s => (
                      <tr key={s.student_id}>
                        <td>{s.student_name}</td>
                        <td>{s.current_average ? `${s.current_average.toFixed(1)}%` : 'N/A'}</td>
                        <td>
                          <span className="result-badge fail">
                            {(s.weak_probability * 100).toFixed(0)}%
                          </span>
                        </td>
                        <td>{s.attendance_rate ? `${s.attendance_rate.toFixed(1)}%` : 'N/A'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}

        {/* STUDENTS */}
        {activeTab === 'students' && (
          <DataTable
            title="Students"
            data={students}
            columns={[
              s => `${s.first_name} ${s.last_name}`,
              s => s.email,
              s => `${s.class_name} ${s.section}`,
              s => s.roll_number,
              s => s.admission_date ? new Date(s.admission_date).toLocaleDateString() : 'N/A',
            ]}
          />
        )}

        {/* TEACHERS */}
        {activeTab === 'teachers' && (
          <DataTable
            title="Teachers"
            data={teachers}
            columns={[
              t => `${t.first_name} ${t.last_name}`,
              t => t.email,
              t => t.department || 'N/A',
              t => t.qualification || 'N/A',
              t => t.phone || 'N/A',
            ]}
          />
        )}

        {/* CLASSES */}
        {activeTab === 'classes' && (
          <DataTable
            title="Classes"
            data={classes}
            columns={[
              c => c.class_name,
              c => c.section || 'N/A',
              c => c.academic_year,
              c => c.teacher_name || 'Not Assigned',
              c => c.student_count,
            ]}
          />
        )}

      </div>
    </div>
  );
}

/* reusable table */
function DataTable({ title, data, columns }) {
  return (
    <div className="marks-section">
      <h2>{title} Management</h2>
      <div className="table-container">
        <table className="data-table">
          <tbody>
            {data.map((row, i) => (
              <tr key={row.id || i}>
                {columns.map((col, j) => (
                  <td key={j}>{col(row)}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminDashboard;
