import React, { useState, useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import Login from "./components/Login";

import AdminDashboard from "./dashboards/AdminDashboard";
import TeacherDashboard from "./dashboards/TeacherDashboard";
import StudentDashboard from "./dashboards/StudentDashboard";
import ParentDashboard from "./dashboards/ParentDashboard";

import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Load user from localStorage on app start
  useEffect(() => {
    try {
      const storedUser = localStorage.getItem("user");

      if (storedUser) {
        const parsedUser = JSON.parse(storedUser);

        setUser({
          ...parsedUser,
          role: parsedUser.role?.toLowerCase(),
        });
      }
    } catch (error) {
      console.error("Invalid user in storage. Clearing...");
      localStorage.clear();
    } finally {
      setLoading(false);
    }
  }, []);

  // Handle login
  const handleLogin = (userData) => {
    if (!userData?.role) return;

    const normalizedUser = {
      ...userData,
      role: userData.role.toLowerCase(),
    };

    setUser(normalizedUser);

    localStorage.setItem("user", JSON.stringify(normalizedUser));
    if (userData.token) {
      localStorage.setItem("token", userData.token);
    }
  };

  // Handle logout
  const handleLogout = () => {
    setUser(null);
    localStorage.clear();
  };

  // Map roles to dashboards
  const dashboardMap = {
    admin: AdminDashboard,
    teacher: TeacherDashboard,
    student: StudentDashboard,
    parent: ParentDashboard,
  };

  const DashboardComponent = dashboardMap[user?.role];

  // Loading screen
  if (loading) {
    return (
      <div
        style={{
          height: "100vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          fontSize: "18px",
        }}
      >
        Loading...
      </div>
    );
  }

  return (
    <Routes>
      {/* Login Route */}
      <Route
        path="/login"
        element={
          !user ? (
            <Login onLogin={handleLogin} />
          ) : (
            <Navigate to="/dashboard" replace />
          )
        }
      />

      {/* Protected Dashboard Route */}
      <Route
        path="/dashboard/*"
        element={
          user && DashboardComponent ? (
            <DashboardComponent user={user} onLogout={handleLogout} />
          ) : (
            <Navigate to="/login" replace />
          )
        }
      />

      {/* Default Redirect */}
      <Route
        path="/"
        element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
      />

      {/* Fallback */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
}

export default App;



