// frontend/src/dashboards/TeacherDashboard.js
import React, { useState, useEffect } from "react";
import { teacherAPI, adminAPI } from "../api"; // fixed import path
import "./TeacherDashboard.css";

function TeacherDashboard({ user, onLogout }) {
  const [activeTab, setActiveTab] = useState("overview");
  const [classes, setClasses] = useState([]);
  const [selectedClass, setSelectedClass] = useState(null);
  const [performance, setPerformance] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setError("");
      const classesRes = await adminAPI.getClasses();
      const classesData = classesRes.data || [];
      setClasses(classesData);

      if (classesData.length > 0) {
        const classId = classesData[0].id;
        setSelectedClass(classId);
        const perfRes = await teacherAPI.getClassPerformance(classId);
        setPerformance(perfRes.data || []);
      }
    } catch (error) {
      console.error("Error:", error);
      setError("Failed to load data");
    } finally {
      setLoading(false);
    }
  };

  const loadClassPerformance = async (classId) => {
    try {
      setLoading(true);
      const perfRes = await teacherAPI.getClassPerformance(classId);
      setPerformance(perfRes.data || []);
    } catch (error) {
      console.error("Class performance error:", error);
      setError("Failed to load class performance");
    } finally {
      setLoading(false);
    }
  };

  const handleClassSelect = async (classId) => {
    setSelectedClass(classId);
    await loadClassPerformance(classId);
  };

  if (loading && activeTab === "overview") {
    return (
      <div className="dashboard">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>Teacher Dashboard</h1>
          <div className="user-info">
            <span>{user?.email}</span>
            <button onClick={onLogout} className="logout-btn">
              Logout
            </button>
          </div>
        </div>
      </header>

      {error && <div className="error-banner">{error}</div>}

      <div className="dashboard-tabs">
        <button
          className={`tab ${activeTab === "overview" ? "active" : ""}`}
          onClick={() => setActiveTab("overview")}
        >
          Overview
        </button>
        <button
          className={`tab ${activeTab === "marks" ? "active" : ""}`}
          onClick={() => setActiveTab("marks")}
        >
          Marks Entry
        </button>
        <button
          className={`tab ${activeTab === "attendance" ? "active" : ""}`}
          onClick={() => setActiveTab("attendance")}
        >
          Attendance
        </button>
      </div>

      <div className="dashboard-content">
        <div className="stats-grid">
          <div className="stat-card">
            <h3>Total Classes</h3>
            <div className="stat-value">{classes.length}</div>
          </div>
          <div className="stat-card">
            <h3>Selected Class Students</h3>
            <div className="stat-value">{performance.length}</div>
          </div>
        </div>

        {/* Class Selector */}
        {classes.length > 0 && (
          <div className="class-selector">
            <h2>Select Class</h2>
            <div className="classes-grid">
              {classes.map((cls) => (
                <button
                  key={cls.id}
                  className={`class-card ${
                    selectedClass === cls.id ? "selected" : ""
                  }`}
                  onClick={() => handleClassSelect(cls.id)}
                >
                  <h4>{cls.class_name}</h4>
                  <p>{cls.section}</p>
                  <p>{cls.student_count || 0} students</p>
                  {cls.teacher_name && <p>{cls.teacher_name}</p>}
                </button>
              ))}
            </div>
          </div>
        )}

        {activeTab === "overview" && (
          <div className="marks-section">
            <h2>Class Performance {selectedClass && "(Selected Class)"}</h2>
            <div className="table-container">
              <table className="data-table">
                <thead>
                  <tr>
                    <th>Roll No</th>
                    <th>Student Name</th>
                    <th>Average %</th>
                    <th>Subjects</th>
                    <th>Total Exams</th>
                    <th>Attendance</th>
                  </tr>
                </thead>
                <tbody>
                  {performance.map((student) => (
                    <tr key={student.id}>
                      <td>{student.roll_number}</td>
                      <td>{student.student_name}</td>
                      <td>
                        <span
                          className={`percentage ${
                            parseFloat(student.average_percentage || 0) >= 50
                              ? "pass"
                              : "fail"
                          }`}
                        >
                          {parseFloat(student.average_percentage || 0).toFixed(1)}%
                        </span>
                      </td>
                      <td>{student.subjects_taken || 0}</td>
                      <td>{student.total_exams || 0}</td>
                      <td>
                        {student.present_days}/{student.total_days}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Placeholder for other tabs */}
        {activeTab === "marks" && (
          <div className="marks-section">
            <h2>Marks Entry (Coming Soon)</h2>
            <p>Marks entry form for selected class will go here</p>
          </div>
        )}

        {activeTab === "attendance" && (
          <div className="marks-section">
            <h2>Attendance Marking (Coming Soon)</h2>
            <p>Attendance marking interface will go here</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default TeacherDashboard;
